mapper.update:
update tbl_list
set title = #{title}, description = #{description}
where listNumber = #{listNumber}

mapper.select:
select listNumber, title, writer, description, regTimestamp, completedTimestamp
from tbl_list
where listNumber = #{listNumber}

mapper.update:
update tbl_list
set completed = 1, completedTimestamp = now()
where listNumber = #{listNumber}
